-- About 2 hours.

-- Reproject it to 3575
-- DROP TABLE IF EXISTS osm_landcover_polygons_3575;
CREATE TABLE osm_landcover_polygons_3575 AS
    SELECT id, osm_id, landuse, leisure, "natural", wetland, ST_Area(geometry) area, ST_MakeValid(geometry) geometry FROM (SELECT id, osm_id, landuse, leisure, "natural", wetland, (ST_Dump(ST_Transform(geometry, 3575))).geom AS geometry
        FROM osm_landcover_polygons_4326) AS x;

CREATE INDEX ON osm_landcover_polygons_3575 USING gist (geometry);

ANALYZE osm_landcover_polygons_3575;

